import { useEffect, useState } from "react";
import { Letter } from "../comps/Portal";

const Contact = () => {
  const [letterClass, setLetterClass] = useState("LetterAnimation");
  const title = ["C", "o", "n", "t", "a", "c", "t", " ", "m", "e"];

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLetterClass("LetterAnimation-hover");
    }, 6000);
    return () => {
      clearTimeout(timeoutId);
    };
  }, []);

  return (
    <div className="container contact">
      <div className="text">
        <h1>
          <Letter letterClass={letterClass} strArray={title} idx={11} />
        </h1>
        <p>
          Lorem ipsum dolor, sit amet consectetur adipisicing elit. Quos,
          libero? Similique velit libero quis id suscipit cumque, quisquam
          necessitatibus, adipisci dicta, optio iusto quia officiis odit
          architecto dolor itaque aspernatur!
        </p>
        <div className="form">
          <form>
            <ul className="list-unstyled row justify-content-between">
              <li className="md:w-1/3 w-full">
                <input
                  type="text"
                  name="name"
                  placeholder="Names..."
                  required
                />
              </li>
              <li className="md:w-2/3 w-full">
                <input
                  type="email"
                  name="email"
                  placeholder="Email..."
                  required
                />
              </li>
              <li className="w-full">
                <input
                  type="text"
                  name="subject"
                  placeholder="Subject..."
                  required
                />
              </li>
              <li className="w-full">
                <textarea
                  name="message"
                  id="message"
                  placeholder="Message Content..."
                ></textarea>
              </li>
              <li className="w-full">
                <input type="submit" className="button" value="Send" />
              </li>
            </ul>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
