import { LetterAnimation, Transition } from "../comps/Portal";
import React, { useEffect, useState } from "react";
import { TypeAnimation } from "react-type-animation";
import { Link } from "react-router-dom";

const Home = () => {
  const [showCursor, setShowCursor] = useState(true);

  const onAnimationFinish = () => {
    setShowCursor(false);
  };

  return (
    <Transition>
      <div className="contain">
        <div>
          <h1>
            <TypeAnimation
              sequence={"Hello there! <br>  I'm D  <br> who you lookin' for."}
              wrapper="p"
              speed={50}
              repeat={0}
              onFinished={onAnimationFinish}
              cursor={showCursor ? true : false}
            />
          </h1>
          <h2 className="cursor-default tracking-[0.5em] text-slate-400 text-xs my-5">
            <span className="transition-all hover:shadow hover:text-slate-50">
              Front End Developer
            </span>{" "}
            /{" "}
            <span className="transition-all hover:shadow hover:text-slate-50">
              Freelancer
            </span>
          </h2>
          <div className="mt-12">
            <Link to="/contact" className="button">
              Say Hello <i className="fa-regular fa-hand-wave"></i>
            </Link>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Home;
