import { LetterAnimation, Transition } from "../comps/Portal";
import React, { useEffect, useState } from "react";
import { TypeAnimation } from "react-type-animation";
import { Link } from "react-router-dom";

const Home = () => {
  const [typeDone1, setTypeDone1] = useState(true);
  const [typeDone2, setTypeDone2] = useState(true);
  const [typeDone3, setTypeDone3] = useState(true);

  // setTimeout(() => {
  //   setTypeDone1(false);
  //   console.log(typeDone1);
  // }, 8000);

  // setTimeout(() => {
  //   setTypeDone2(false);
  //   console.log(typeDone2);
  // }, 6000);

  // setTimeout(() => {
  //   setTypeDone3(false);
  //   console.log(typeDone3);
  // }, 6000);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setTypeDone1(false);
      console.log(typeDone1);
    }, 800);
    return () => {
      clearTimeout(timeoutId);
    };
  }, []);

  return (
    <Transition>
      <div className="contain">
        <div>
          <h1 className="">
            <TypeAnimation
              sequence={"Hello there!"}
              wrapper="span"
              speed={50}
              repeat={0}
              cursor={typeDone1}
            />
            <br />

            <TypeAnimation
              sequence={"I'm D"}
              wrapper="span"
              speed={50}
              repeat={0}
              cursor={typeDone1}
            />
            <br />

            <TypeAnimation
              sequence={"who you lookin' for."}
              wrapper="span"
              speed={50}
              repeat={0}
              cursor={typeDone1}
            />
          </h1>
          <h2 className="cursor-default tracking-[0.5em] text-slate-400 text-xs my-5">
            <span className="transition-all hover:shadow hover:text-slate-50">
              Front End Developer
            </span>{" "}
            /{" "}
            <span className="transition-all hover:shadow hover:text-slate-50">
              Freelancer
            </span>
          </h2>
          <div className="mt-12">
            <Link to="/contact" className="button">
              Say Hello <i className="fa-regular fa-hand-wave"></i>
            </Link>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Home;
