import { useState, useEffect } from "react";

const LetterAnimation = ({ strArray, idx = 1 }) => {
  const [letterClass, setLetterClass] = useState("text-animate");

  useEffect(() => {
    if (strArray.length > 0) {
      strArray.forEach((char, i) => {
        if (i === strArray.length - 1) {
          setTimeout(() => {
            setLetterClass("textletter");
          }, strArray.length * 1000); // Change to "textletter" after strArray.length seconds
        }
      });
    }
  }, [strArray]);

  return (
    <span>
      {strArray.map((char, i) => (
        <span
          key={char + i}
          className={`LetterAnimation ${letterClass} _${i + idx}`}
        >
          {char}
        </span>
      ))}
    </span>
  );
};

export default LetterAnimation;
