import { LetterAnimation, Transition } from "../comps/Portal";
import React, { useEffect, useState } from "react";
import { TypeAnimation } from "react-type-animation";
import { Link } from "react-router-dom";

const Home = () => {
  const [letterClass, setLetterClass] = useState("LetterAnimation");
  const helloThere = [
    "H",
    "e",
    "l",
    "l",
    "o",
    " ",
    "t",
    "h",
    "e",
    "r",
    "e",
    "!",
  ];
  const myName = ["I", "'", "m", " ", "D"];
  const whoYou = [
    "w",
    "h",
    "o",
    " ",
    "y",
    "o",
    "u",
    " ",
    "l",
    "o",
    "o",
    "k",
    "i",
    "n",
    "g",
    " ",
    "f",
    "o",
    "r",
    ".",
  ];

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLetterClass("LetterAnimation-hover");
    }, 6000);
    return () => {
      clearTimeout(timeoutId);
    };
  }, []);

  return (
    <Transition>
      <div className="contain">
        <div>
          <h1>
            <TypeAnimation
              sequence={[
                // Same substring at the start will only be typed out once, initially
                "We produce food for Mice",
                1000, // wait 1s before replacing "Mice" with "Hamsters"
                "We produce food for Hamsters",
                1000,
                "We produce food for Guinea Pigs",
                1000,
                "We produce food for Chinchillas",
                1000,
              ]}
              wrapper="span"
              speed={50}
              style={{ fontSize: "2em", display: "inline-block" }}
              repeat={Infinity}
            />
            <LetterAnimation
              letterClass={letterClass}
              strArray={helloThere}
              idx={11}
            />
            <br />
            <LetterAnimation
              letterClass={letterClass}
              strArray={myName}
              idx={25}
            />
            <br />
            <LetterAnimation
              letterClass={letterClass}
              strArray={whoYou}
              idx={34}
            />
          </h1>
          <h2 className="cursor-default tracking-[0.5em] text-slate-400 text-xs my-5">
            <span className="transition-all hover:shadow hover:text-slate-50">
              Front End Developer
            </span>{" "}
            /{" "}
            <span className="transition-all hover:shadow hover:text-slate-50">
              Freelancer
            </span>
          </h2>
          <div className="mt-12">
            <Link to="/contact" className="button">
              Say Hello <i className="fa-regular fa-hand-wave"></i>
            </Link>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Home;
